def TLV_DER_encode_int(i):
  l = (i.bit_length() + 7 // 8) // 8 + 1
  l_str = hex2(l)
  if l > 2**7  // 4:
    l_str += '8' + str(len(l_str) // 2)
  return '02' + l_str + '00' +  hex2(i)



